import Data from './data/machineSite.json';
import Layer from './data/layer.json'
import rice from './assets/rice.png'
import Map from './components/baseMap'
import { MapContainer, TileLayer, Marker,Polyline,Popup, Tooltip } from 'react-leaflet'
import L from 'leaflet';
import { useEffect, useState } from 'react';


function App() {
    const position = [13.824869, 109.146137]
    const [map,setMap] = useState("https://{s}.tile.opentopomap.org/{z}/{x}/{y}.png ")
    const [status, setFilterMap] = useState("map1")

    const myIcon = L.icon({
      iconUrl: rice,
      iconRetinaUrl: rice,
      iconAnchor: [5, 55],
      popupAnchor: [10, -44],
      iconSize: [50, 50],
  });


  function handleSetMap() {
    switch(status){
      case 'map1': 
      setMap("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png")
      setFilterMap("map1")
        break
      case 'map2':
      setMap('https://{s}.tile.opentopomap.org/{z}/{x}/{y}.png')
      setFilterMap("map2")
        break
    }
  }
  useEffect(()=>{
    handleSetMap()
  },[map, status])
  console.log(map)
    return (

        <MapContainer style={{ height: '100vh', width: '100wh' }} center={position} zoom={12} scrollWheelZoom={true}>

          <TileLayer
            attribution='&copy; <a href="http://osm.org/copyright"></a> contributors'
            url={map}
          />

            {Data.map(data =>(
                <Marker  key={data.id}  icon={myIcon} center={[data.gps.latitude,data.gps.longtitude]} position={[data.gps.latitude,data.gps.longtitude]} radius={800}>
                  <Popup>
                    {data.name}
                 </Popup>
                 {/* <Tooltip direction="center" style={{fontSize:10}} fontSize={10} opacity={10} permanent>
                  {data.name}
                </Tooltip> */}
                </Marker>
            ))}

              <Map setMap = {handleSetMap}/>
            {Layer.map(data =>(
                <Polyline key={data.id} className = "background-container"  pathOptions={data.style} positions={data.coordinates}>
              <Popup>
                    {data.name}
                 </Popup>
                </Polyline>
            ))}
        </MapContainer>

    );
  }

  export default App;
