import logo from './logo.svg';
import './App.css';
import 'leaflet/dist/leaflet.css';

import Data from './data/machineSite.json';
import Layer from './data/layer.json'

import { MapContainer,Circle, TileLayer,SVGOverlay, Marker, Polyline,Popup } from 'react-leaflet'
import L from 'leaflet';
function App() {
  const position = [13.824869, 109.146137]
  const myIcon = L.icon({
    iconUrl: require('./assets/rice.svg'),
    iconSize: [64,64],
    iconAnchor: [32, 64],
    popupAnchor: null,
    shadowUrl: null,
    shadowSize: null,
    shadowAnchor: null
});

  return (

      <MapContainer   style={{ height: '100vh', width: '100wh' }} center={position} zoom={12} scrollWheelZoom={true}>
        <TileLayer
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

          {Data.map(data =>(
              <Marker  center={[data.gps.latitude,data.gps.longtitude]} position={[data.gps.latitude,data.gps.longtitude]} radius={800}>
                <Popup>
                  {data.name}
               </Popup>
              </Marker>
          ))}

          {Layer.map(data =>(
              <Polyline className = "background-container"  pathOptions={data.style} positions={data.coordinates}>

              </Polyline>
          ))}



      </MapContainer>

  );
}

export default App;
