import Data from './data/machineSite.json';
import Layer from './data/layer.json'
import rice from './assets/rice.png'
import {  iconPerson  } from './Icon';
import { MapContainer,Circle, TileLayer,SVGOverlay, Marker,Polyline,Popup, Tooltip } from 'react-leaflet'
import L from 'leaflet';
function App() {
  const position = [13.824869, 109.146137]
  const myIcon = L.icon({
    iconUrl: rice,
    iconRetinaUrl: rice,
    iconAnchor: [5, 55],
    popupAnchor: [10, -44],
    iconSize: [50, 50],
});

  return (

      <MapContainer style={{ height: '100vh', width: '100wh' }} center={position} zoom={12} scrollWheelZoom={true}>
        <TileLayer
          attribution='&copy; <a href="http://osm.org/copyright"></a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

          {Data.map(data =>(
              <Marker icon={myIcon} center={[data.gps.latitude,data.gps.longtitude]} position={[data.gps.latitude,data.gps.longtitude]} radius={800}>
                <Popup>
                  {data.name}
               </Popup>
               <Tooltip direction="center" style={{fontSize:10}} opacity={10} permanent>
                {data.name}
              </Tooltip>
              </Marker>
          ))}

          {Layer.map(data =>(
              <Polyline className = "background-container"  pathOptions={data.style} positions={data.coordinates}>

              </Polyline>
          ))}



      </MapContainer>

  );
}

export default App;
