import logo from './logo.svg';
import './App.css';
import 'leaflet/dist/leaflet.css';

import Data from './data/machineSite.json';
import Layer from './data/layer.json'

import { MapContainer,Circle, TileLayer,SVGOverlay, Marker, Polyline,Popup,L } from 'react-leaflet'
function App() {
  const position = [13.824869, 109.146137]
  var greenIcon = L.icon({
    iconUrl: './assets/rice.png',
    iconSize:     [38, 95], // size of the icon
    shadowSize:   [50, 64], // size of the shadow
    iconAnchor:   [22, 94], // point of the icon which will correspond to marker's location
    shadowAnchor: [4, 62],  // the same for the shadow
    popupAnchor:  [-3, -76] // point from which the popup should open relative to the iconAnchor
});
  return (
    <div className="App">
      <MapContainer   style={{ height: '100vh', width: '100wh' }} center={position} zoom={12} scrollWheelZoom={true}>
        <TileLayer
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

          {Data.map(data =>(
              <Marker icon={greenIcon} center={[data.gps.latitude,data.gps.longtitude]} position={[data.gps.latitude,data.gps.longtitude]} radius={800}>
                <Popup>
                  {data.name}
               </Popup>
              </Marker>
          ))}

          {Layer.map(data =>(
              <Polyline className = "background-container"  pathOptions={data.style} positions={data.coordinates}>

              </Polyline>
          ))}



      </MapContainer>
    </div>
  );
}

export default App;
